import React from "react";
import MovieStatus from "../movieStatus/MovieStatus";
import SearchMovie from "../searchMovie/SearchMovie";
import MovieList from "../movieList/MovieList";
import AddMovie from "../addMovie/AddMovie";

import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [
        {
          name: "Ertugrul",
          viewers: 9090,
          favourite: false,
          like: false,
          id: 0,
        },
        {
          name: "Urdugan",
          viewers: 1090,
          favourite: false,
          like: false,
          id: 0,
        },
        {
          name: "Umar Ibn Hattob",
          viewers: 9090,
          favourite: false,
          like: false,
          id: 0,
        },
      ],
    };
  }
  render() {
    return (
      <main className="vh-100 d-flex align-content-center flex-column">
        <MovieStatus />
        <div className="content">
          <SearchMovie />
          <MovieList />
          <AddMovie />
        </div>
      </main>
    );
  }
}

export default App;
