import React from "react";
import MovieStatus from "../movieStatus/MovieStatus";
import SearchMovie from "../searchMovie/SearchMovie";
import MovieList from "../movieList/MovieList";
import AddMovie from "../addMovie/AddMovie";

import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [
        {
          name: "Ertugrul",
          viewers: 9090,
          favourite: true,
          like: false,
          id: 0,
        },
        {
          name: "Urdugan",
          viewers: 1090,
          favourite: false,
          like: true,
          id: 0,
        },
        {
          name: "Umar Ibn Hattob",
          viewers: 9890,
          favourite: false,
          like: false,
          id: 0,
        },
      ],
    };
  }

  addMovie = (e) => {
    e.preventDefault();
    const newMovie = {
      name: "Umar Ibn Hattob1",
      viewers: 98910,
      favourite: false,
      like: false,
      id: 0,
    };
    this.setState(({ data }) => {
      const newArray = [...data, newMovie];

      data = newArray;
      return {
        data,
      };
    });
  };

  render() {
    const { data } = this.state;

    return (
      <main className="vh-100 d-flex align-content-center flex-column p-2">
        <div className="content">
          <MovieStatus />
          <SearchMovie />
          <MovieList data={data} />
          <AddMovie addMovie={this.addMovie} />
        </div>
      </main>
    );
  }
}

export default App;
